import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(false);

  // Fungsi untuk mengekstrak nomor dan caption dari nama file
  const extractNumberAndCaption = (filename) => {
    const nameWithoutExt = filename.split('.')[0];
    const parts = nameWithoutExt.split('_');
    const number = parseInt(parts[0]) || 0;
    const caption = parts.slice(1).join(' ');
    return { number, caption };
  };

  const handleFileChange = (e) => {
    const fileArray = Array.from(e.target.files).sort((a, b) => {
      const numA = extractNumberAndCaption(a.name).number;
      const numB = extractNumberAndCaption(b.name).number;
      return numA - numB;
    });
    setFiles(fileArray);
  };

  const handleUpload = async () => {
    try {
      if (files.length === 0) {
        alert('Pilih file terlebih dahulu');
        return;
      }

      setLoading(true);
      const formData = new FormData();

      files.forEach((file) => {
        formData.append('images', file);
      });

      const response = await axios.post('http://localhost:3001/convert', formData, {
        responseType: 'blob',
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'converted.pdf');
      document.body.appendChild(link);
      link.click();
      link.remove();
      window.URL.revokeObjectURL(url);
    } catch (error) {
      console.error('Error:', error);
      alert('Terjadi kesalahan saat mengkonversi file');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Image to PDF Converter</h1>

      <div className="mb-4">
        <input
          type="file"
          multiple
          accept="image/*"
          onChange={handleFileChange}
          className="border p-2"
        />
      </div>

      {files.length > 0 && (
        <div className="mb-4">
          <h2 className="text-lg font-semibold mb-2">Selected Files:</h2>
          <ul className="list-disc pl-5">
            {files.map((file, index) => {
              const { number, caption } = extractNumberAndCaption(file.name);
              return (
                <li key={index}>
                  Gambar {number}. {caption}
                </li>
              );
            })}
          </ul>
        </div>
      )}

      <button
        onClick={handleUpload}
        disabled={loading || files.length === 0}
        className="bg-blue-500 text-white px-4 py-2 rounded disabled:bg-gray-400"
      >
        {loading ? 'Converting...' : 'Convert to PDF'}
      </button>
    </div>
  );
}

export default App;
